(61, 'Sarnia Jail', '700 Christina St. N.###Sarnia, ON, N7V 3C2', '519-337-3261', '', '<p class=\"MsoNoSpacing\"><b>Do I need to be on\r\nan Incarcerated persons visitors list?&nbsp;</b><br></p>\r\n\r\n<p class=\"MsoNoSpacing\">No</p>\r\n\r\n<p class=\"MsoNoSpacing\"><o:p>&nbsp;</o:p></p>\r\n\r\n<p class=\"MsoNoSpacing\"><b>How can I schedule\r\na visit?<o:p></o:p></b></p>\r\n\r\n<p class=\"MsoNoSpacing\">Book appointments 24 hrs in advance.&nbsp; You maybe denied your visit if you arrive in\r\nthe last 15 minutes of a visiting session.&nbsp;\r\n</p>\r\n\r\n<p class=\"MsoNoSpacing\"><b>&nbsp;</b></p>\r\n\r\n<p class=\"MsoNoSpacing\"><b>Visitation\r\nInformation:</b>&nbsp; </p>\r\n\r\n<p class=\"MsoNoSpacing\">Length of visit:&nbsp;\r\n30 minutes </p>\r\n\r\n<p class=\"MsoNoSpacing\">Number of visits per week: &nbsp;2 </p>\r\n\r\n<p class=\"MsoNoSpacing\">Number of visitors per visit: 2 (not including infants)</p>\r\n\r\n<p class=\"MsoNoSpacing\">Can I drop off money to my loved one when I visit?$100\r\n(Cash only)&nbsp;&nbsp;</p>', '<p class=\"MsoNoSpacing\"><span style=\"font-weight: 700;\">Regular Visiting Hours:</span>&nbsp; On even days of the month:</p><p class=\"MsoNoSpacing\">General Population have visits from 1:00-4:00PM</p><p class=\"MsoNoSpacing\">Kitchen workers have visits from 6:00-7:00PM</p><p class=\"MsoNoSpacing\"><o:p>&nbsp;</o:p></p><p class=\"MsoNoSpacing\"><span style=\"font-weight: 700;\">Regular Visiting Hours:</span>&nbsp; On odd days of the month:</p><p class=\"MsoNoSpacing\">Protective Custody have visits from 1:00-4:00PM</p><p class=\"MsoNoSpacing\">Female Prisoners have visits from:&nbsp; 5:00-6:30PM</p><p class=\"MsoNoSpacing\">* Be sure to call ahead as visitation hours may change</p><p class=\"MsoNoSpacing\"><br></p>', 'sarnia-jail', '20181220103652-Sarnia.jpg', 1, '1', '2018-03-20 15:51:41'),
(62, 'South West Detention Centre (Windsor Jail)', '4819 8th Concession Road, Maidstone, ON, N0R 1K0', '519-967-3100', '', '<p class=\"MsoNoSpacing\">The South West Detention Centre, also known as Windsor Jail became operational in 2014. &nbsp;The new jail will replace Chatham Jail and the previous Windsor Jail. &nbsp;The South West Detention Centre is a 315 bed maximum security facility that accommodates both male and female offenders.</p><p class=\"MsoNoSpacing\"><b><br></b></p><p class=\"MsoNoSpacing\"><b>Regular Visiting\r\nHours:</b>&nbsp;</p>\r\n\r\n<p class=\"MsoNoSpacing\">Visiting hours vary, call ahead to find out</p>\r\n\r\n<p class=\"MsoNoSpacing\"><o:p>&nbsp;</o:p></p>\r\n\r\n<p class=\"MsoNoSpacing\"><b>Do I need to be on\r\nan Incarcerated persons visitors list?&nbsp; <o:p></o:p></b></p>\r\n\r\n<p class=\"MsoNoSpacing\">No</p>\r\n\r\n<p class=\"MsoNoSpacing\"><o:p>&nbsp;</o:p></p>\r\n\r\n<p class=\"MsoNoSpacing\"><b>How can I schedule\r\na visit?<o:p></o:p></b></p>\r\n\r\n<p class=\"MsoNoSpacing\">Book appointments 24 hrs in advance.&nbsp; </p>\r\n\r\n<p class=\"MsoNoSpacing\"><b>&nbsp;</b></p>\r\n\r\n<p class=\"MsoNoSpacing\"><b>Visitation\r\nInformation (Videocamera Visitations):</b>&nbsp;\r\n</p>\r\n\r\n<p class=\"MsoNoSpacing\">Length of visit:&nbsp;\r\n150 minutes/week (remand), 25 minutes/week (protective custody or\r\nsegregation) </p>\r\n\r\n<p class=\"MsoNoSpacing\">Number of visits per week: &nbsp;3 (remand), 2 (protective custody or\r\nsegregation) </p>\r\n\r\n<p class=\"MsoNoSpacing\">Number of visitors per visit: 2 (not including infants) </p>\r\n\r\n<p class=\"MsoNoSpacing\">Can I drop off money to my loved one when I visit?$50\r\n(Cash only) Monday-Friday: 8:00AM - 3:00PM.&nbsp;&nbsp;</p>', '<p>Visiting Hours: </p><p>3rd floor custody male population: Monday, 9:00-11:00am, Thursday,\r\n2:00-4:00pm, Sunday, 2:00-4:00pm. </p><p>1st floor custody male population: Monday, 5:00-\r\n7:00pm. Wednesday, 2:00-4:00pm, Saturday, 9:00-11:00am. </p><p>Male dorm population:\r\nTuesday &amp; Saturday 2:00-4:00pm. </p><p>Male Protective Custody: Monday, 9:00-11:00am,\r\nTuesday, 5:00-7:00pm, Friday, 9:00-11:00am, Sunday, 9:00-11:00am. </p><p>Female population:\r\nWednesday, 5:00-7:00pm and Friday, 2:00-4:00pm.</p>', 'south-west-detention-centre', '20181220103715-windsor jail.jpg', 1, '1', '2018-03-20 15:51:41'),
